package practice_codes.hackerrank;

import org.junit.jupiter.api.Assertions;
import org.junit.jupiter.api.Test;

public class VowelSubstringTest {

    @Test
    void checkSubstring() {
        Assertions.assertEquals(1, VowelSubstring.findSubstring("aeoibddaeoiud"));
        Assertions.assertEquals(2, VowelSubstring.findSubstring("aeoibsddaeiouudb"));
    }

    @Test
    void invalidCase() {
        Assertions.assertEquals(0, VowelSubstring.findSubstring("sndalsndlaisdlwlda   ksmldiamdka"));
        Assertions.assertEquals(0, VowelSubstring.findSubstring(""));
    }

    @Test
    void worstCase() {
        StringBuilder s1 = new StringBuilder();
        s1.append("");
        s1.append("");
        s1.append("");
        s1.append("");
        s1.append("");
        s1.append("");
        Assertions.assertEquals(1308, VowelSubstring.findSubstring(s1.toString()));
        s1.replace(0,s1.length(),"a");
        Assertions.assertEquals(0, VowelSubstring.findSubstring(s1.toString()));
    }

}
